                })->where('kode_kegiatan', "2.19")->first())->id,
                "kode_sub_kegiatan" => 1,
                "nama_sub_kegiatan" => "Fasilitasi Pemenuhan Persyaratan Perolehan Izin  Pengelolaan Terminal untuk Kepentingan Sendiri  (TUKS)    di    dalam    DLKR/DLKP    Pelabuhan  Pengumpan    Lokal   dalam    Sistem    Pelayanan  Perizinan Berusaha Terintegrasi Secara Elektronik"
            ],
            [
                "id_kegiatan" => optional(\App\Models\Kegiatan::whereHas("Program", function ($q) {
                    $q->where("kode_program", $this->convertKode(3));
                    $q->whereHas("BidangUrusan", function ($q) {
                        $q->where("kode_bidang_urusan", $this->convertKode(15));
                        $q->whereHas("Urusan", function ($q) {
                            $q->where("kode_urusan", 2);
                        });
                    });
                })->where('kode_kegiatan', "2.19")->first())->id,
                "kode_sub_kegiatan" => 2,
                "nama_sub_kegiatan" => "Koordinasi     dan     Sinkronisasi     Pengawasan  Pelaksanaan  Izin  Pengelolaan  Terminal  untuk  Kepentingan Sendiri (TUKS) di dalam DLKR/DLKP  Pelabuhan Pengumpan Lokal"
            ]
        ];

        foreach ($sub_kegiatan AS $index => $sk){
            $sub_kegiatan[$index]['uuid'] = \Illuminate\Support\Str::uuid()->toString();
            $sub_kegiatan[$index]['tahun'] = date('Y');
            $kegiatan = \App\Models\Kegiatan::with('Program.BidangUrusan.Urusan')->find($sk['id_kegiatan']);
            $sub_kegiatan[$index]['kode_sub_kegiatan'] = $kegiatan ? $kegiatan->Program->BidangUrusan->Urusan->kode_urusan.'.'.$kegiatan->Program->BidangUrusan->kode_bidang_urusan.'.'.$kegiatan->Program->kode_program.'.'.$kegiatan->kode_kegiatan.'.'.$this->convertKode($sk['kode_sub_kegiatan']) : null;
        }
        \App\Models\SubKegiatan::insert($sub_kegiatan);
    }

    public function convertKode($kode)
    {
        return $kode < 10 ? "0" . $kode : $kode;
    }
}
